import React, { useState, useEffect } from "react";  // Mengimpor useState
import AOS from "aos";
import "aos/dist/aos.css";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Calculator from "./components/Calculator";
import About from "./components/About";
import Footer from "./components/Footer";

function App() {
  // Menggunakan useState untuk mendeklarasikan state
  const [counter, setCounter] = useState(0);  // State untuk menyimpan counter

  // Menggunakan useEffect untuk inisialisasi AOS
  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  // Fungsi untuk memperbarui state counter
  const incrementCounter = () => {
    setCounter(counter + 1);  // Mengupdate state counter
  };

  return (
    <div className="bg-gray-100">
      <Navbar />
      <Hero />
      <Calculator />
      <About />
      <Footer />

      {/* Tombol untuk memperbarui counter */}
      <div className="mt-4 text-center">
        <button 
          onClick={incrementCounter} 
          className="p-2 text-white bg-blue-500 rounded"
        >
          Increase Counter
        </button>
        <p className="mt-2">Counter: {counter}</p>
      </div>
    </div>
  );
}

export default App;
