import React, { useState, useEffect } from "react";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

// Mendaftarkan elemen yang digunakan
ChartJS.register(ArcElement, Tooltip, Legend);

const Calculator = () => {
  const [investment, setInvestment] = useState("");
  const [profit, setProfit] = useState("");
  const [roi, setRoi] = useState(0);

  const calculateROI = () => {
    const roiValue = ((profit - investment) / investment) * 100;
    setRoi(roiValue.toFixed(2));
  };

  // Data chart yang bergantung pada input
  const data = {
    labels: ["Biaya Investasi", "Keuntungan Bersih"],
    datasets: [
      {
        data: [investment, profit - investment],
        backgroundColor: ["#f59e0b", "#10b981"],
      },
    ],
  };

  useEffect(() => {
    if (investment === "" || profit === "") {
      setRoi(0);
    }
  }, [investment, profit]); // Update chart saat input berubah

  return (
    <section id="kalkulator" className="px-6 py-16 text-white bg-gray-900">
      <div className="max-w-3xl mx-auto text-center">
        <h2 className="text-3xl font-bold" data-aos="fade-up">Kalkulator ROI</h2>
        <div className="flex flex-col mt-6 space-y-4" data-aos="fade-right">
          <input type="number" placeholder="Biaya Investasi" className="p-3 bg-gray-800 rounded" 
            value={investment} onChange={(e) => setInvestment(e.target.value)} />
          <input type="number" placeholder="Keuntungan Bersih" className="p-3 bg-gray-800 rounded" 
            value={profit} onChange={(e) => setProfit(e.target.value)} />
          <button onClick={calculateROI} className="p-3 bg-yellow-500 rounded">Hitung ROI</button>
        </div>
        <div className="mt-6">
          <h3 className="text-xl">Hasil ROI: <span className="text-green-400">{roi}%</span></h3>
          <Doughnut data={data} />
        </div>
      </div>
    </section>
  );
};

export default Calculator;
